import takeoff_marker from "./img/takeoff_marker.png";
import mapboxgl from 'mapbox-gl';

function rotateFigureCenter(cords, center, phi){
    phi = phi*Math.PI/180
    let rotatedFigure = []

    for(let pair of cords){
        let x = pair[0]-center[0]
        let y = pair[1]-center[1]
        let x_rot = x*Math.cos(phi) - y*Math.sin(phi)
        let y_rot = x*Math.sin(phi) + y*Math.cos(phi)
        rotatedFigure.push([
          x_rot+center[0], y_rot+center[1]
        ])
    }
    return rotatedFigure
  }

export class CommonManager{
    constructor(map, manager3D, update_modal_message){
        this.map = map 
        this.manager3D = manager3D
        this.translate_cursor = false 
        this.start_marker = null
        this.update_modal_message = update_modal_message
        this.polygon = null
        this.conture = {
            markers:[],
            cords:[],
            edges:[],
            source:{
              'type': 'geojson',
              'data': {
                'type': 'Feature',
                'geometry': {
                'type': 'Polygon',
                'coordinates': [
                    []
                  ]
                }
              }
            },
            layer_area:{
              'id': "conture",
              'type': 'fill',
              'source': "conture",
              'layout': {},
              'paint': {
                'fill-color': '#008000',
                'fill-opacity': 0.5
                }
              },
              layer_line:{
                'id': 'line_conture',
                'type': 'line',
                'source': "conture",
                'layout': {},
                'paint': {
                'line-color': '#000',
                'line-width': 2
                }
              }
          }
          this.markup = {
            cords:[],
            source:{
              'type': 'geojson',
              'data': {
                'type': 'Feature',
                'geometry': {
                'type': 'LineString',
                'coordinates': [
                    []
                  ]
                }
              }
            },
            layer:{
              'id': "markup",
              'type': 'line',
              'source': "markup",
              'layout': {},
              'paint': {
              'line-color': '#fff',
              'line-width': 2
              }
            }
          };
    }

    toggleCursor(){
        if (this.translate_cursor === true){
            this.translate_cursor = false 
        }else{
            this.translate_cursor = true
        }
    }

    setStartMarker(coords){
        if (this.start_marker === null){
            this.start_marker = new mapboxgl.Marker(
                {
                    draggable: true
                }
            ).setLngLat(coords).addTo(this.map)
            let icon = document.createElement('img');
            icon.src = takeoff_marker;
            icon.width = 35
