import "./Wheather.scss";

const Wheather = (props) => {
    const settings = [
        {key: "temperature", title: "Температура", min: -40, max: 80, type:"range", step: 1},
        {key: "wind_speed", title: "Скорость ветра", min: 0, max: 40, type: "range", step: 1},
        {key: "humidity", title: "Влажность", min: 0, max: 100, type: "range", step: 1},
        {key: "adjuvants", title: "Адъюванты", type: "checkbox"}
    ]
    return <ul className="wheather block">
        {settings.map((item, index) => {
            if (item.type === "range"){
                return <li key={index}>
                    <div className="move_title"><p>{item.title}</p></div>
                    <input type="range"
                        min={item.min}
                        max={item.max}
                        step={item.step}
                        value={props.settings[item.key]}
                        onChange={(e) => {props.changeMove(item.key, e.target.value)}}
                    />
                    <input type="number"
                        min={item.min}
                        max={item.max}
                        step={item.step}
                        value={props.settings[item.key]}
                        onChange={(e) => {props.changeMove(item.key, e.target.value)}}
                    />
                </li>
            }else if (item.type === "select"){
                return <li key={index} className="big">
                    <div className="move_title"><p>{item.title}</p></div>
                    <select onChange={(e) => {props.changeMove(item.key, e.target.value)}}>
                        {item.values.map((value, v_index) => {
                            return <option key={v_index} value={value}>{item.options[v_index]}</option>
                        })}
                    </select>
                </li>
            }
        })}
    </ul>
}
export default Wheather
