import { useEffect } from "react";
import "./Wheather.scss";

const Wheather = (props) => {
    const settings = [
        {key: "temperature", title: "Температура воздуха", min: 0, max: 40, type:"range", step: 1},
        {key: "wind_speed", title: "Скорость ветра", min: 0, max: 40, type: "range", step: 1},
        {key: "humidity", title: "Влажность", min: 30, max: 90, type: "range", step: 10},
        {key: "adjuvants", title: "Адъюванты", type: "checkbox"}
    ]
    const conditions = {"good": "Предпочтительные", "middle": "Пограничные", "bad": "Неблагоприятные"}
    const checkHumTemp = () => {
        let hum = Number(props.settings.humidity)
        let temp = Number(props.settings.temperature)
        const temp_hum_table = {
            90: {good: [18, 30], middle: [33, 36], bad: []},
            80: {good: [18, 30], middle: [33, 36], bad: []},
            70: {good: [18, 30], middle: [33, 36], bad: []},
            60: {good: [18, 24], middle: [27, 33], bad: [36, 80]},
            50: {good: [], middle: [18, 27], bad: [30, 80]},
            40: {good: [], middle: [18, 21], bad: [24, 80]},
            30: {good: [], middle: [18, 21], bad: [24, 80]},
        }
        let hum_values = temp_hum_table[hum]
        let result = "undef"
        Object.keys(hum_values).forEach(key => {
            let range = hum_values[key]
            if (temp >= range[0] && temp <= range[1]){
                result = key
            }
        })
        return result
    }

    useEffect(() => {
        console.log(checkHumTemp())
    })

    return <ul className="wheather block">
        {settings.map((item, index) => {
            if (item.type === "range"){
                return <li key={index}>
                    <div className="wheather_title"><p>{item.title}</p></div>
                    <input type="range"
                        min={item.min}
                        max={item.max}
                        step={item.step}
                        value={props.settings[item.key]}
                        onChange={(e) => {props.changeWheather(item.key, e.target.value)}}
                    />
                    <input type="number"
                        min={item.min}
                        max={item.max}
                        step={item.step}
                        value={props.settings[item.key]}
                        onChange={(e) => {props.changeWheather(item.key, e.target.value)}}
                    />
                </li>
            }else if (item.type === "checkbox"){
                return <li key={index} className="big">
                    <div className="wheather_title"><p>{item.title}</p></div>
                    <input type="checkbox" 
                        value={props.settings.adjuvants}
                        onChange={(e) => {props.changeWheather(item.key, e.target.value)}}
                    />
                </li>
            }
        })}
        <li className="conditions">
            <div className="wheather_title"><p>Условия</p></div>
            <div className={"result "+checkHumTemp()}><p>{conditions[checkHumTemp()]}</p></div>
        </li>
    </ul>
}
export default Wheather
