import "./Wheather.scss";

const Wheather = (props) => {
    const settings = [
        {key: "temperature", title: "Температура воздуха", min: -40, max: 80, type:"range", step: 1},
        {key: "wind_speed", title: "Скорость ветра", min: 0, max: 40, type: "range", step: 1},
        {key: "humidity", title: "Влажность", min: 0, max: 100, type: "range", step: 1},
        {key: "adjuvants", title: "Адъюванты", type: "checkbox"}
    ]
    const checkHumTemp = () => {
        let hum = Number(props.settings.humidity)
        let temp = Number(props.settings.temperature)
        const temp_hum_table = {
            90: {temp: {18: "good", 21: "good", 24: "good", 27: "good", 30: "good", 33: "middle", 36: "middle"}},
            80: {temp: {18: "good", 21: "good", 24: "good", 27: "good", 30: "good", 33: "middle", 36: "middle"}},
            70: {temp: {18: "good", 21: "good", 24: "good", 27: "good", 30: "good", 33: "middle", 36: "middle"}},
            60: {temp: {18: "good", 21: "good", 24: "good", 27: "middle", 30: "middle", 33: "middle", 36: "bad"}},
            50: {temp: {18: "middle", 21: "middle", 24: "middle", 27: "middle", 30: "bad", 33: "bad", 36: "bad"}},
            40: {temp: {18: "middle", 21: "middle", 24: "bad", 27: "bad", 30: "bad", 33: "bad", 36: "bad"}},
            30: {temp: {18: "middle", 21: "middle", 24: "bad", 27: "bad", 30: "bad", 33: "bad", 36: "bad"}}
        }
        try { return temp_hum_table[hum].temp[temp] }
        catch {return ""}
    }
    return <ul className="wheather block">
        {settings.map((item, index) => {
            if (item.type === "range"){
                return <li key={index}>
                    <div className="wheather_title"><p>{item.title}</p></div>
                    <input type="range"
                        min={item.min}
                        max={item.max}
                        step={item.step}
                        value={props.settings[item.key]}
                        onChange={(e) => {props.changeWheather(item.key, e.target.value)}}
                    />
                    <input type="number"
                        min={item.min}
                        max={item.max}
                        step={item.step}
                        value={props.settings[item.key]}
                        onChange={(e) => {props.changeWheather(item.key, e.target.value)}}
                    />
                </li>
            }else if (item.type === "checkbox"){
                return <li key={index} className="big">
                    <div className="wheather_title"><p>{item.title}</p></div>
                    <input type="checkbox" 
                        value={props.settings.adjuvants}
                        onChange={(e) => {props.changeWheather(item.key, e.target.value)}}
                    />
                </li>
            }
        })}
        <li className="conditions">
            <div className="wheather_title"><p>Условия</p></div>
            <div className="result good"><p>Предпочтительные</p></div>
        </li>
    </ul>
}
export default Wheather
