import "./Spraying.scss";
import Settings from "./Settings/Settings";
import { CommonManager } from "./CommonManager";
import { useEffect, useState } from "react";

const Spraying = (props) => {
    const [CommonManager_obj, setManager] = useState(null)
    const [cursor_coords, setCursorCoords] = useState([0, 0])
    useEffect(() => {
        if (props.manager3D !== null){
            let manager = new CommonManager(props.map, props.manager3D)
            setManager(manager)
            props.map.on("mousemove", (e) => {
                if (manager.translate_cursor === true){
                   setCursorCoords([e.lngLat.lng, e.lngLat.lat]) 
                }
            })
        }
    }, [props.manager3D])

    return <div className="spraying">
        <Settings manager={CommonManager_obj} cursor_coords={cursor_coords}/>
    </div>
}
export default Spraying
